mapper.select:

select 

  from ccp_live_user
  where user_id = #{userId,jdbcType=VARCHAR}


    and  live_activity = #{liveActivity,jdbcType=VARCHAR}


  limit 1

mapper.insert:

insert into ccp_live_user (live_user_id, user_id, live_activity, 
  user_real_name, user_telephone, user_create_time, 
  user_update_time, user_upload_img, user_is_like
  )
values (#{liveUserId,jdbcType=VARCHAR}, #{userId,jdbcType=VARCHAR}, #{liveActivity,jdbcType=VARCHAR}, 
  #{userRealName,jdbcType=VARCHAR}, #{userTelephone,jdbcType=VARCHAR}, #{userCreateTime,jdbcType=TIMESTAMP}, 
  #{userUpdateTime,jdbcType=TIMESTAMP}, #{userUploadImg,jdbcType=VARCHAR}, #{userIsLike,jdbcType=INTEGER}
  )
