mapper.select:

  	SELECT count(*)  FROM ccp_vote_item i 

	WHERE i.vote_id=#{voteId} and i.item_is_del =1 


		and vote_item_id=#{voteItemId}

mapper.insert:

insert into ccp_vote_item (vote_item_id, item_name, item_img_url, 
  author_name, author_head_img_url, item_create_time, 
  item_create_user, vote_id, item_update_time, 
  item_update_user, item_is_del, item_link
  )
values (#{voteItemId,jdbcType=VARCHAR}, #{itemName,jdbcType=VARCHAR}, #{itemImgUrl,jdbcType=VARCHAR}, 
  #{authorName,jdbcType=VARCHAR}, #{authorHeadImgUrl,jdbcType=VARCHAR}, #{itemCreateTime,jdbcType=TIMESTAMP}, 
  #{itemCreateUser,jdbcType=VARCHAR}, #{voteId,jdbcType=VARCHAR}, #{itemUpdateTime,jdbcType=TIMESTAMP}, 
  #{itemUpdateUser,jdbcType=VARCHAR}, #{itemIsDel,jdbcType=INTEGER}, #{itemLink,jdbcType=VARCHAR}
  )
