mapper.select:
SELECT a.*, caus.yearBrowseCount as yearBrowseCount, ct.tag_name FROM cms_activity a LEFT JOIN cms_activity_venue_relevance r ON a.activity_id=r.activity_id LEFT JOIN cms_venue v on v.venue_id=r.venue_id LEFT JOIN cms_tag ct ON ct.tag_id = a.activity_type LEFT JOIN cms_activity_event ce ON a.ACTIVITY_ID = ce.ACTIVITY_ID LEFT JOIN (select activity_id, count(activity_id) as yearBrowseCount from cms_activity_user_statistics where OPERATE_TYPE=1 AND DATE_SUB(CURDATE(), INTERVAL 1 YEAR) <= CREATE_TIME group by activity_id) as caus on caus.ACTIVITY_ID = a.activity_id WHERE 1=1 AND a.activity_is_del = 1 AND a.activity_state = 6 AND cast(a.ACTIVITY_END_TIME as datetime) <= #{activityEndTime,jdbcType=VARCHAR}
AND ASSN_ID=#{assnId}
GROUP BY a.ACTIVITY_ID
order by yearBrowseCount desc

SELECT a.*, caus.yearBrowseCount as yearBrowseCount, ct.tag_name,CASE WHEN st.activity_star is NULL then 0 ELSE st.ACTIVITY_STAR end activity_star FROM cms_activity a LEFT JOIN cms_activity_venue_relevance r ON a.activity_id=r.activity_id LEFT JOIN cms_activity_star st ON a.activity_id=st.activity_id LEFT JOIN cms_venue v on v.venue_id=r.venue_id LEFT JOIN cms_tag ct ON ct.tag_id = a.activity_type LEFT JOIN cms_activity_event ce ON a.ACTIVITY_ID = ce.ACTIVITY_ID LEFT JOIN (select activity_id, count(activity_id) as yearBrowseCount from cms_activity_user_statistics where OPERATE_TYPE=1 AND DATE_SUB(CURDATE(), INTERVAL 1 YEAR) <= CREATE_TIME group by activity_id) as caus on caus.ACTIVITY_ID = a.activity_id WHERE 1=1 AND a.activity_is_del = 1 AND a.activity_state = 6 AND cast(a.ACTIVITY_END_TIME as datetime) <= #{activityEndTime,jdbcType=VARCHAR}
AND ASSN_ID=#{assnId}
GROUP BY a.ACTIVITY_ID
order by yearBrowseCount desc
limit ${resultFirst},${resultSize}
