mapper.insert:

insert into ccp_information_type (INFORMATION_TYPE_ID, TYPE_NAME, TYPE_CREATE_USER, 
  TYPE_UPDATE_USER, TYPE_CREATE_TIME, TYPE_UPDATE_TIME,INFORMATION_MODULE_ID,TYPE_PARENT_ID
  )
values (#{informationTypeId,jdbcType=VARCHAR}, #{typeName,jdbcType=VARCHAR}, #{typeCreateUser,jdbcType=VARCHAR}, 
  #{typeUpdateUser,jdbcType=VARCHAR}, #{typeCreateTime,jdbcType=TIMESTAMP}, #{typeUpdateTime,jdbcType=TIMESTAMP},
  #{informationModuleId,jdbcType=VARCHAR},#{typeParentId, jdbcType=VARCHAR}
  )

mapper.select:


  	select count(*)

	from ccp_information i 

	where i.INFORMATION_STATUS = 0 and i.INFORMATION_TYPE= #{typeId}

  	select * from ccp_information_type where 1=1

      and INFORMATION_MODULE_ID = #{informationModuleId,jdbcType=VARCHAR}


      and TYPE_CREATE_USER in (${userIds})

  	order by TYPE_CREATE_TIME

		limit ${firstResult},${rows}

	select count(*) from ccp_information_type where 1=1

    and INFORMATION_MODULE_ID = #{informationModuleId,jdbcType=VARCHAR}


    and TYPE_CREATE_USER in (${userIds})
