mapper.select:
	  select count(*)
	  from ccp_musicessay_article cma
	  left join ccp_musicessay_user cmu on cma.user_id=cmu.user_id
	  where 1=1

      and cma.article_type = #{articleType,jdbcType=INTEGER}


      and cmu.user_real_name like #{userRealName,jdbcType=VARCHAR}


      and cmu.user_moblie_no like #{userMoblieNo,jdbcType=VARCHAR}


      and cma.article_is_del = #{articleIsDel,jdbcType=INTEGER}

select cma.article_title, cma.article_id, cma.article_text, cma.article_create_time, cma.article_is_del, cma.article_like, cmu.user_real_name, cmu.user_moblie_no from ccp_musicessay_article cma left join ccp_musicessay_user cmu on cma.user_id=cmu.user_id where 1=1
and cma.article_type = #{articleType,jdbcType=INTEGER}
and cmu.user_real_name like #{userRealName,jdbcType=VARCHAR}
and cmu.user_moblie_no like #{userMoblieNo,jdbcType=VARCHAR}
and cma.article_is_del = #{articleIsDel,jdbcType=INTEGER}
order by cma.article_create_time desc
limit ${firstResult},${rows}
