mapper.select:
select 
count(*)
from ccp_special_enter
where 1=1

	and enter_is_del=#{enterIsDel}


     AND project_id = #{projectId}

order by enter_create_time

  limit #{firstResult},#{rows}


  select 
   e.enter_id, e.enter_name, e.enter_logo_image_url, e.enter_parame_path,e.enter_is_del, e.project_id, 
e. enter_create_time, e.enter_create_user,p.project_name
 from ccp_special_enter e
 left join ccp_special_project p on e.project_id = p.project_id
  where 1=1

 	and enter_is_del=#{enterIsDel}


      AND e.project_id = #{projectId}

 order by enter_create_time


   limit #{firstResult},#{rows}
