mapper.insert:

insert into ccp_culture_contest_answer (culture_answer_id, culture_user_id, stage_number, 
  answer_create_time, answer_status, answer_time, 
  user_group_type, answer_use_time, answer_right_number, 
  answer_right_question, answer_number
  )
values (#{cultureAnswerId,jdbcType=VARCHAR}, #{cultureUserId,jdbcType=VARCHAR}, #{stageNumber,jdbcType=INTEGER}, 
  #{answerCreateTime,jdbcType=TIMESTAMP}, #{answerStatus,jdbcType=INTEGER}, #{answerTime,jdbcType=INTEGER}, 
  #{userGroupType,jdbcType=INTEGER}, #{answerUseTime,jdbcType=TIMESTAMP}, #{answerRightNumber,jdbcType=INTEGER}, 
  #{answerRightQuestion,jdbcType=LONGVARCHAR}, #{answerNumber,jdbcType=LONGVARCHAR}
  )

mapper.select:
select a.* from ccp_culture_contest_answer a
inner join ccp_culture_contest_user u on u.culture_user_id = a.culture_user_id
where 1 = 1
and u.user_id = #{userId}
and a.stage_number = #{stageNumber}
order by a.answer_right_number desc



  	select * from (

		SELECT @rowno := @rowno + 1 AS rowno ,a.*,tu.user_name,tu.user_head_img_url FROM (

		SELECT * FROM (

		SELECT * FROM `ccp_culture_contest_answer`  a

		WHERE 1 = 1

		and a.`stage_number`= #{stageNumber}


		 and a.`user_group_type`= #{userGroupType}

		and a.answer_status = 1

		ORDER BY a.answer_right_number DESC ,a.answer_use_time ASC 	
		) a 

		GROUP BY a.culture_user_id

		ORDER BY a.answer_right_number DESC,a.answer_use_time ASC


		LIMIT #{limit}


		)a

		LEFT JOIN `ccp_culture_contest_user` u ON a.culture_user_id = u.culture_user_id

		LEFT JOIN `cms_terminal_user` tu ON tu.user_id= u.user_id

		,(SELECT @rowno := 0) t


		 ) a where a.culture_user_id= #{cultureUserId}



  	select * from (


		SELECT  @rowno := @rowno + 1 AS rowno,a.*,tu.user_name,tu.user_head_img_url FROM (

		SELECT SUM(a.answer_right_number) as rightSum,a.* FROM (

		SELECT * FROM (

		SELECT * FROM `ccp_culture_contest_answer`  a

		WHERE 1 = 1


		 and a.`user_group_type`= #{userGroupType}


		and a.answer_status = 1

		ORDER BY a.answer_right_number DESC ,a.answer_use_time ASC 	

		) a 

		GROUP BY a.stage_number,a.culture_user_id

		)a  GROUP BY a.culture_user_id

		ORDER BY SUM(a.answer_right_number) DESC ,a.answer_use_time ASC 	


		LIMIT #{limit}

		)a

		LEFT JOIN `ccp_culture_contest_user` u ON a.culture_user_id = u.culture_user_id

		LEFT JOIN `cms_terminal_user` tu ON tu.user_id= u.user_id

		,(SELECT @rowno := 0) t


		 ) a where a.culture_user_id= #{cultureUserId}
