mapper.select:
select count(*) from ccp_musicessay_article where 1 = 1
and article_type = #{articleType}
and user_id = #{userId}
and article_is_del = 0


  SELECT * FROM (

SELECT @rowno := @rowno + 1 AS rowno ,a.* FROM (

 SELECT a.* ,u.USER_NAME AS userName

	,u.USER_HEAD_IMG_URL AS userHeadImgUrl

 FROM(
SELECT a.* FROM ccp_musicessay_article a WHERE 1=1 AND a.article_is_del = 0 AND a.article_type = 1 ORDER BY a.article_like DESC

	) a

	  LEFT JOIN cms_terminal_user u ON u.user_id = a.user_id	


GROUP BY a.user_id

 ORDER BY a.article_like DESC

   ) a,(SELECT @rowno := 0) t

)a  WHERE a.user_id=#{userId}
