SELECT a.ACTIVITY_ID, a.ACTIVITY_NAME, a.ACTIVITY_IS_FREE, a.ACTIVITY_PRICE, a.ACTIVITY_START_TIME, a.ACTIVITY_END_TIME, a.ACTIVITY_AREA, v.venue_id AS venue_id, v.venue_name AS venue_name, v.venue_type AS venue_type, v.venue_area AS venue_area, u.user_account AS user_account, a.ACTIVITY_TIME_DES , cao.ORDER_IS_VALID , cao.ORDER_PAY_STATUS, cao.ACTIVITY_ORDER_ID, cao.ORDER_PHONE_NO, cae.event_date, cae.event_time, cae.event_date_time as eventDateTimes, cao.ORDER_VOTES, cao.order_number AS order_number, cao.ORDER_CREATE_TIME as orderCreateTime, a.CREATE_ACTIVITY_CODE, a.ACTIVITY_SALES_ONLINE, t.user_name as order_name, cao.order_check_time, su.user_account as order_check_user FROM cms_activity_order cao left join cms_terminal_user t on cao.user_id = t.user_id LEFT JOIN cms_activity a on a.activity_id= cao.ACTIVITY_ID LEFT JOIN cms_activity_venue_relevance r on a.activity_id=r.activity_id LEFT JOIN cms_venue v on v.venue_id=r.venue_id LEFT JOIN sys_user u on a.activity_create_user=u.user_id LEFT JOIN sys_user su on cao.sys_user_id=su.user_id LEFT JOIN cms_activity_event cae on cao.event_id = cae.event_id and cae.activity_id = cao.activity_id where 1=1 and cao.ORDER_IS_VALID != 3
AND a.ACTIVITY_ID = #{activityId,jdbcType=INTEGER}
AND cao.ORDER_NUMBER like #{orderNumber,jdbcType=VARCHAR}
AND a.ACTIVITY_STATE = #{activityState,jdbcType=INTEGER}
AND cao.ORDER_VOTES like #{OrderVotes,jdbcType=INTEGER}
AND substring(cae.event_date_time,1,16) < #{eventDateTimes} and o.ORDER_PAY_STATUS not in (2,4)
AND cao.ORDER_PAY_STATUS = #{orderPayStatus,jdbcType=VARCHAR}
order by cao.ORDER_CREATE_TIME desc

