mapper.select:
SELECT a.INTEGRAL_ID ,a.INTEGRAL_CHANGE,a.CREATE_TIME,a.CHANGE_TYPE,a.INTEGRAL_FROM,a.INTEGRAL_TYPE
from  sys_user_integral_detail  a

where 1=1


  and a.INTEGRAL_ID = #{integralId,jdbcType=VARCHAR}



  and a.CREATE_TIME >= #{beforeDate,jdbcType=DATE}


order by a.CREATE_TIME desc

  limit ${firstResult},${rows}

SELECT count(*)
from  sys_user_integral_detail  a


  where a.INTEGRAL_ID = #{integralId,jdbcType=VARCHAR}

