mapper.select:
SELECT c.USER_ID, c.TUSER_ID, c.ORDER_NO, c.USER_TEL, c.VALID_CODE, c.BOOK_STATUS, c.ORDER_CREATE_TIME, c.ROOM_ID, c.VENUE_ID, c.ROOM_ORDER_ID, c.ORDER_UPDATE_TIME, c.CHECK_STATUS, t.TUSER_NAME as tuser_team_name, t.T_CREATE_TIME, r.ROOM_NAME, r.ROOM_IS_FREE, v.venue_city, v.venue_area, v.venue_address, v.venue_name, r.room_no, r.room_pic_url, r.room_fee, rb.OPEN_PERIOD as open_period, rb.CUR_DATE as cur_date FROM cms_room_order c LEFT JOIN cms_team_user t on c.TUSER_ID = t.TUSER_ID LEFT JOIN cms_venue v on c.VENUE_ID = v.VENUE_ID LEFT JOIN cms_room r on c.ROOM_ID = r.ROOM_ID LEFT JOIN cms_room_book rb on c.BOOK_ID = rb.BOOK_ID where 1=1 AND c.BOOK_STATUS in (0,1) AND NOW() < CONCAT(rb.cur_date," ",left(rb.open_period,5))
AND c.USER_ID = #{userId}
order by c.ORDER_CREATE_TIME desc limit ${firstResult},${rows}

SELECT c.USER_ID, c.TUSER_ID, c.ORDER_NO, c.USER_TEL, c.VALID_CODE, 4 as BOOK_STATUS, c.ORDER_CREATE_TIME as orderCreateTime, c.ROOM_ID, c.VENUE_ID, c.ROOM_ORDER_ID, c.ORDER_UPDATE_TIME, c.CHECK_STATUS, t.TUSER_NAME as tuser_team_name, t.T_CREATE_TIME, r.ROOM_NAME, r.ROOM_IS_FREE, v.venue_city, v.venue_area, v.VENUE_ADDRESS, v.VENUE_NAME, r.room_no, r.ROOM_PIC_URL, rb.OPEN_PERIOD as open_period, rb.CUR_DATE as cur_date FROM cms_room_order c LEFT JOIN cms_team_user t on c.TUSER_ID = t.TUSER_ID LEFT JOIN cms_venue v on c.VENUE_ID = v.VENUE_ID LEFT JOIN cms_room r on c.ROOM_ID = r.ROOM_ID LEFT JOIN cms_room_book rb on c.BOOK_ID = rb.BOOK_ID where 1=1 AND c.BOOK_STATUS in (0,1) AND NOW() > CONCAT(rb.cur_date," ",left(rb.open_period,5))
AND c.USER_ID = #{userId}
union all SELECT c.USER_ID, c.TUSER_ID, c.ORDER_NO, c.USER_TEL, c.VALID_CODE, c.BOOK_STATUS, c.ORDER_CREATE_TIME as orderCreateTime, c.ROOM_ID, c.VENUE_ID, c.ROOM_ORDER_ID, c.ORDER_UPDATE_TIME, c.CHECK_STATUS, t.TUSER_NAME as tuser_team_name, t.T_CREATE_TIME, r.ROOM_NAME, r.ROOM_IS_FREE, v.venue_city, v.venue_area, v.VENUE_ADDRESS, v.VENUE_NAME, r.room_no, r.ROOM_PIC_URL, rb.OPEN_PERIOD as open_period, rb.CUR_DATE as cur_date FROM cms_room_order c LEFT JOIN cms_team_user t on c.TUSER_ID = t.TUSER_ID LEFT JOIN cms_venue v on c.VENUE_ID = v.VENUE_ID LEFT JOIN cms_room r on c.ROOM_ID = r.ROOM_ID LEFT JOIN cms_room_book rb on c.BOOK_ID = rb.BOOK_ID where 1=1 AND c.BOOK_STATUS in (2,3,5,6)
AND c.USER_ID = #{userId}
order by orderCreateTime desc limit ${firstResult},${rows}

select sum(recordCount) from ( SELECT count(c.room_order_id) recordCount FROM cms_room_order c LEFT JOIN cms_team_user t on c.TUSER_ID = t.TUSER_ID LEFT JOIN cms_venue v on c.VENUE_ID = v.VENUE_ID LEFT JOIN cms_room r on c.ROOM_ID = r.ROOM_ID LEFT JOIN cms_room_book rb on c.BOOK_ID = rb.BOOK_ID where 1=1 AND c.BOOK_STATUS = 1 AND NOW() > CONCAT(rb.cur_date," ",left(rb.open_period,5))
AND c.USER_ID = #{userId}
union all SELECT count(c.room_order_id) recordCount FROM cms_room_order c LEFT JOIN cms_team_user t on c.TUSER_ID = t.TUSER_ID LEFT JOIN cms_venue v on c.VENUE_ID = v.VENUE_ID LEFT JOIN cms_room r on c.ROOM_ID = r.ROOM_ID LEFT JOIN cms_room_book rb on c.BOOK_ID = rb.BOOK_ID where 1=1 AND c.BOOK_STATUS in (2,3,5,6)
AND c.USER_ID = #{userId}
) t

select ro.ROOM_ORDER_ID,ro.USER_ID,ro.USER_NAME,ro.USER_TEL,ro.TUSER_ID, tus.TUSER_NAME as tuser_team_name,ro.ORDER_NO,ro.VENUE_ID,ro.ROOM_ID,ro.ROOM_ID, ro.BOOK_STATUS,ro.BOOK_ID,ro.VALID_CODE,ro.ORDER_CREATE_TIME,ro.ORDER_UPDATE_TIME, rb.OPEN_PERIOD as OPEN_PERIOD, concat(rb.cur_date," ",left(rb.open_period,5)) as room_open_time, su.USER_NICK_NAME as SYS_USER_NAME from cms_room_order ro left join cms_team_user tus on ro.TUSER_ID = tus.TUSER_ID left join sys_user su on ro.SYS_USER_ID = su.USER_ID left join cms_room_book rb on ro.BOOK_ID = rb.BOOK_ID where 1=1
AND ro.ROOM_ORDER_ID = #{roomOrderId}
AND ro.ROOM_ID = #{roomId}
AND ro.ORDER_NO LIKE #{orderNo}
order by ro.ORDER_UPDATE_TIME desc limit ${firstResult},${rows}

select count(ro.ROOM_ORDER_ID)
from cms_room_order ro
left join
cms_team_user tus on ro.TUSER_ID = tus.TUSER_ID
left join cms_room_book
rb on ro.BOOK_ID = rb.BOOK_ID
where 1=1

	AND ro.ROOM_ORDER_ID = #{roomOrderId}


	AND ro.ROOM_ID = #{roomId}


	AND ro.ORDER_NO LIKE #{orderNo}

SELECT COUNT(cro.ORDER_NO) as order_num, r.room_id as ROOM_ID, r.ROOM_NO as ROOM_No, r.ROOM_NAME as room_name, r.ROOM_AREA as room_Area, r.ROOM_CAPACITY as room_Capacity FROM cms_room_order cro RIGHT JOIN ( SELECT c.room_id, c.ROOM_NAME, c.ROOM_NO, c.ROOM_AREA, c.ROOM_CAPACITY FROM cms_room c LEFT JOIN cms_venue v ON c.ROOM_VENUE_ID = v.VENUE_ID WHERE 1=1
AND v.VENUE_ID = #{venueId}
) r ON cro.ROOM_ID = r.room_id and cro.BOOK_STATUS=1 GROUP BY r.ROOM_ID
limit ${firstResult},${rows}

SELECT c.ROOM_ORDER_ID, c.BOOK_STATUS, c.ORDER_NO, c.USER_TEL, c.VALID_CODE, c.CHECK_STATUS, t.TUSER_NAME as tuser_team_name, r.ROOM_NAME, v.venue_address, v.venue_name, rb.OPEN_PERIOD as open_period, rb.CUR_DATE as cur_date FROM cms_room_order c LEFT JOIN cms_team_user t on c.TUSER_ID = t.TUSER_ID LEFT JOIN cms_venue v on c.VENUE_ID = v.VENUE_ID LEFT JOIN cms_room r on c.ROOM_ID = r.ROOM_ID LEFT JOIN cms_room_book rb on c.BOOK_ID = rb.BOOK_ID where 1=1
AND c.BOOK_STATUS = #{bookStatus,jdbcType=INTEGER}
AND c.VALID_CODE = #{validCode}

SELECT right(cv.VENUE_CITY,3) as city, right(cv.VENUE_AREA,3) as area, cro.ORDER_NO, cro.ROOM_ORDER_ID, cv.VENUE_NAME, cv.VENUE_ADDRESS, cro.ORDER_CREATE_TIME, cr.ROOM_NAME, cr.ROOM_FEE, cr.ROOM_ID, cro.VALID_CODE, cro.CHECK_STATUS, cr.ROOM_PIC_URL, ctu.TUSER_NAME as tuser_team_name, cro.VENUE_ID, count(c.COMMENT_ID) AS commentNums, rb.OPEN_PERIOD AS open_period, rb.CUR_DATE AS cur_date FROM cms_room_order cro LEFT JOIN cms_venue cv ON cro.VENUE_ID = cv.VENUE_ID LEFT JOIN cms_team_user ctu ON cro.TUSER_ID = ctu.TUSER_ID LEFT JOIN cms_comment c ON cro.VENUE_ID = c.COMMENT_RK_ID LEFT JOIN cms_room cr ON cr.ROOM_ID = cro.ROOM_ID LEFT JOIN cms_room_book rb ON cro.BOOK_ID = rb.BOOK_ID WHERE 1 = 1
AND cro.USER_ID = #{userId}
AND cro.BOOK_STATUS = 1
AND concat(rb.CUR_DATE," ",substring(rb.OPEN_PERIOD,1,5))>= #{roomTime}
GROUP BY cro.ROOM_ORDER_ID ORDER BY cro.ORDER_UPDATE_TIME DESC
limit ${firstResult},${rows}

SELECT cro.ORDER_NO, cro.ROOM_ORDER_ID, cv.VENUE_NAME, cv.VENUE_ADDRESS, cr.ROOM_ID, rb.CUR_DATE, rb.OPEN_PERIOD, cro.BOOK_STATUS, cro.CHECK_STATUS, cro.ORDER_CREATE_TIME FROM cms_room_order cro LEFT JOIN cms_venue cv ON cro.VENUE_ID = cv.VENUE_ID LEFT JOIN cms_room cr ON cr.ROOM_ID = cro.ROOM_ID LEFT JOIN cms_room_book rb ON cro.BOOK_ID = rb.BOOK_ID WHERE 1 = 1
AND cro.USER_ID = #{userId}
AND (concat(rb.CUR_DATE," ",substring(rb.OPEN_PERIOD,1,5))< #{roomTime} or cro.BOOK_STATUS in(2,3,5)) AND cro.BOOK_STATUS !=4
GROUP BY cro.ROOM_ORDER_ID ORDER BY cro.ORDER_UPDATE_TIME DESC
limit ${firstResult},${rows}

