mapper.select:
SELECT c.ORDER_NO, c.BOOK_STATUS, c.CHECK_STATUS, ct.TUSER_NAME AS tuser_team_name, c.VALID_CODE, c.ROOM_ORDER_ID, c.USER_NAME, c.USER_TEL, s.DICT_NAME, GROUP_CONCAT(r.ROOM_NAME SEPARATOR ',') AS room_name, v.VENUE_NAME, v.venue_address, GROUP_CONCAT(rb.OPEN_PERIOD SEPARATOR ',') AS open_period, GROUP_CONCAT(rb.CUR_DATE SEPARATOR ',') AS curDates, c.ORDER_CREATE_TIME, r.ROOM_ID FROM cms_room_order c LEFT JOIN cms_room_book rb ON c.BOOK_ID = rb.BOOK_ID LEFT JOIN cms_venue v ON c.VENUE_ID = v.VENUE_ID LEFT JOIN cms_room r ON r.ROOM_ID = c.ROOM_ID LEFT JOIN sys_dict s ON s.DICT_ID = v.VENUE_MOOD LEFT JOIN cms_team_user ct ON ct.TUSER_ID = c.TUSER_ID WHERE 1=1
AND c.VALID_CODE LIKE #{orderValidateCode}
AND c.ORDER_NO LIKE #{orderNumber}
AND v.VENUE_NAME LIKE #{venueName} ESCAPE '/'
AND c.BOOK_STATUS!=4

SELECT cro.ORDER_NO, cro.ROOM_ORDER_ID, cv.VENUE_NAME, cv.VENUE_ADDRESS, cro.ORDER_CREATE_TIME, cr.ROOM_NAME, cr.ROOM_FEE, cr.ROOM_IS_FREE, cr.ROOM_ID, cro.VALID_CODE, cr.ROOM_PIC_URL, ctu.TUSER_NAME as tuser_team_name, cro.VENUE_ID, rb.OPEN_PERIOD AS open_period, rb.CUR_DATE AS curDates, rb.DAY_OF_WEEK AS dayOfWeek, cro.BOOK_STATUS, cro.CHECK_STATUS FROM cms_room_order cro LEFT JOIN cms_venue cv ON cro.VENUE_ID = cv.VENUE_ID LEFT JOIN cms_team_user ctu ON cro.TUSER_ID = ctu.TUSER_ID LEFT JOIN cms_comment c ON cro.VENUE_ID = c.COMMENT_RK_ID LEFT JOIN cms_room cr ON cr.ROOM_ID = cro.ROOM_ID LEFT JOIN cms_room_book rb ON cro.BOOK_ID = rb.BOOK_ID WHERE 1 = 1
AND cro.USER_ID = #{userId}
AND cro.BOOK_STATUS = #{bookStatus}
AND (cro.VALID_CODE LIKE #{orderValidateCode}
OR cro.ORDER_NO LIKE #{orderNumber}
OR cv.VENUE_NAME LIKE #{venueName} escape '/')
