mapper.insert:

insert into cms_user_operator_log (ORDER_LOG_ID, OPERATOR_USER_ID, CREATE_TIME, 
  ORDER_ID, OPERATOR_TYPE, USER_ID, 
  USER_TYPE, TUSER_ID, REMARK
  )
values (#{orderLogId,jdbcType=VARCHAR}, #{operatorUserId,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{orderId,jdbcType=VARCHAR}, #{operatorType,jdbcType=INTEGER}, #{userId,jdbcType=VARCHAR}, 
  #{userType,jdbcType=INTEGER}, #{tuserId,jdbcType=VARCHAR}, #{remark,jdbcType=LONGVARCHAR}
  )

mapper.select:
select ORDER_LOG_ID, OPERATOR_USER_ID, l.CREATE_TIME, ORDER_ID, OPERATOR_TYPE, u.USER_ID, l.USER_TYPE, l.TUSER_ID, l.REMARK, tu.TUSER_NAME,u.USER_NAME,case when l.USER_TYPE=1 then su.USER_NICK_NAME else u1.USER_NICK_NAME end as OPERATOR_USER_NAME from cms_user_operator_log l LEFT JOIN cms_team_user tu ON tu.TUSER_ID = l.TUSER_ID LEFT JOIN cms_terminal_user u ON u.USER_ID = l.USER_ID LEFT JOIN cms_terminal_user u1 ON u1.USER_ID = l.OPERATOR_USER_ID LEFT JOIN sys_user su ON su.USER_ID = l.OPERATOR_USER_ID where 1=1
AND ORDER_ID = #{orderId,jdbcType=VARCHAR}
AND l.USER_ID = #{userId,jdbcType=VARCHAR}
AND l.TUSER_ID = #{tuserId,jdbcType=VARCHAR}
order by CREATE_TIME DESC
