mapper.select:
select e.entry_id, e.project_name, e.project_type, e.company_name, e.linkman, e.telephone, e.email, e.entry_subject, e.project_profile, e.primary_performer, e.secondary_performer, e.staff, e.is_band, e.is_color_sing, e.is_beauty_dance, e.is_large_prop, e.entry_index, e.lecture_time, e.scheme_name, e.video_name, e.video_time, e.entry_create_time, e.entry_create_user, e.entry_update_time, e.entry_update_user, e.entry_complete_time, e.entry_is_complete, e.entry_is_delete, e.technical_check, e.content_check, e.speaker_name, e.suitable_crowd, e.supporting_activity_name, e.arrange_exhibitions_time, e.supporting_activity_time,e.minimum_unfold_number, e.max_unfold_number, e.exhibit_number, e.panel_number, e.routine_maintenance, e.auction_calendar_copy, e.declaration_category, e.fixed_telephone, e.company_detail, e.company_performance, e.copyright, e.entry_description, e.stage_size, e.lighting, e.sound, e.projection, e.derrick, e.rest, e.voice_tube, e.design_scheme, e.stage_img, e.stage_img_name, e.poster_img, e.poster_name, e.video_url, e.video_cover_img, e.video_introduction, e.speaker_identity, e.topic_purport, e.exhibition_outline, e.exhibition_introduction, e.supporting_activity, e.activity_target, e.interactive_mode, e.teaching_experience, e.learning_background, e.teaching_material, e.supplie, e.supporting_activity_content, e.special_design, e.texture, e.measure, e.stage_classroom, e.community_recommend, e.business_license, e.performance_license, e.activity_img, e.loading_time, e.logistics_scale, e.show_schedule, e.other_instruction, e.exhibition_impression_drawing, e.exhibition_impression_drawing_name, e.quality, e.supplie_num,
IF((SELECT count(vote.VOTE_ID) FROM qyg_vote vote WHERE vote.ENTRY_ID = e.ENTRY_ID and vote.USER_ID = #{userId} and DATE_FORMAT(vote.CREATE_TIME, '%Y-%m-%d') = DATE_FORMAT(NOW(), '%Y-%m-%d') )>0,1,0) as IS_VOTE,
(SELECT count(vote.VOTE_ID) FROM qyg_vote vote WHERE vote.ENTRY_ID = e.ENTRY_ID ) as VOTE_COUNT, e.entry_create_time from qyg_project_entry e INNER JOIN qyg_user_account ua ON e.entry_create_user = ua.user_id where 1=1 and e.project_type = 1 AND e.technical_check = 1 AND e.content_check = 1 AND e.entry_is_delete = 0 AND ua.account_status = 1
and e.ENTRY_ID = #{entryId}
and e.entry_subject = #{entrySubject}
group by e.ENTRY_ID order by e.declaration_category,convert(e.project_name USING gbk), VOTE_COUNT DESC, e.ENTRY_CREATE_TIME DESC
limit ${firstResult},${rows}
limit ${firstResult},${rows}
